
export type Etape = {
  titre: string;
  duree: string;
  etapes: string[];
};

export type EtudePrelim = {
    q: string;
    r: string;
};

export type TP = {
  id: number;
  duree: string;
  titre: string;
  situation: string;
  objectif: string;
  materiel: string[];
  etudePrelim: EtudePrelim[];
  activitePratique: Etape[];
  securiteRangement: string[];
  pointsCles: string[];
};

function etape(titre: string, duree: string, etapes: string[]): Etape {
    return { titre, duree, etapes };
}

export const tpPremiere: Record<number, TP> = {
  1:{ id: 1, duree:'2h00', titre:'AS 207 • Accueillir le client et restituer le véhicule', situation:'Un client se présente à l\'atelier sans rendez-vous. Son véhicule est immobilisé. Vous êtes chargé de l\'accueillir, de prendre en charge le véhicule en créant un Ordre de Réparation (OR) complet et de lui expliquer la procédure à suivre.', objectif:'Réaliser un accueil et une restitution professionnels, avec OR et traçabilité. (Compétence C2.1)', materiel:['OR','Grille d’état','Tablette atelier','Documents garantie'], etudePrelim:[{q:'Quelles sont les mentions obligatoires d\'un Ordre de Réparation (OR) ?',r:''},{q:'Quelle est la différence fondamentale entre un devis et une facture ?',r:''},{q:'Comment gérer une demande de prise sous garantie du client ?',r:''}], activitePratique:[ etape('Accueil et Prise en Charge','30 min',['Accueillir le client avec professionnalisme et courtoisie.','Écouter activement les symptômes décrits par le client.','Effectuer une inspection visuelle du véhicule en présence du client.','Rédiger un Ordre de Réparation (OR) complet incluant immatriculation, kilométrage et demande précise.']), etape('Suivi de l\'Intervention','30 min',['Informer le client des délais prévisionnels.','Obtenir un accord écrit pour les travaux complémentaires.']), etape('Restitution du Véhicule','40 min',['Expliquer clairement la facture au client.','Donner des conseils d\'entretien et planifier le prochain rendez-vous.','Archiver le dossier et s\'assurer de la satisfaction du client.']) ], securiteRangement:['Protection des données personnelles (RGPD)','Archivage méthodique des OR','Maintien d\'un espace d\'accueil propre et ordonné'], pointsCles:['Clarté de l\'OR','Traçabilité des interventions','Posture professionnelle'] },
  2:{ id: 2, duree:'1h30', titre:'UE 203 • Utilisation d\'un multimètre', situation:'Un véhicule présente des dysfonctionnements électriques intermittents. Avant tout diagnostic, le chef d\'atelier vous demande de valider votre maîtrise du multimètre sur une maquette puis sur le véhicule.', objectif:'Mesurer tension, résistance et continuité correctement et en sécurité pour préparer un diagnostic. (Compétence C2.1)', materiel:['Multimètre','Pointes de touche','Fusibles de test','Schéma électrique simple'], etudePrelim:[{q:'Définir tension, courant et résistance avec leurs unités de mesure.',r:''},{q:'Quelles sont les précautions à prendre avant toute mesure (contact, polarité, calibre) ?',r:''},{q:'Comment interpréter une chute de tension anormale dans un circuit ?',r:''}], activitePratique:[ etape('Mesures de Tension (V)','25 min',['Mesurer la tension batterie au repos, moteur tournant, et avec consommateurs actifs.','Mesurer les chutes de tension aux bornes des connexions positives et négatives.']), etape('Mesures de Résistance (Ω) et Continuité','30 min',['Tester la continuité d\'un fusible avec la fonction buzzer.','Mesurer la résistance d\'une sonde CTN/CTP à différentes températures.','Contrôler la continuité et l\'isolement d\'un faisceau électrique.']), etape('Validation et Interprétation','20 min',['Remplir un tableau de relevés de mesures.','Analyser les erreurs fréquentes (inversion des pointes, mauvais calibre) et comment les éviter.']) ], securiteRangement:['Couper le contact avant toute mesure de résistance','Ne jamais percer les isolants des fils','Ranger les cordons du multimètre après usage'], pointsCles:['Bon calibre','Polarité correcte','Sécurité électrique'] },
  3:{ id: 3, duree:'1h45', titre:'UE 202 • Contrôle circuit de démarrage', situation:'Un client se plaint que son véhicule refuse de démarrer. Le démarreur ne tourne pas. Vous devez diagnostiquer le circuit de démarrage pour identifier la cause de la panne.', objectif:'Identifier la cause d’un non-démarrage et valider le diagnostic. (Compétence C2.4)', materiel:['Multimètre','Schéma électrique','Pince ampèremétrique','Chargeur de batterie'], etudePrelim:[{q:'Décrire le chemin du courant (+12V) de la batterie au démarreur.',r:''},{q:'Quel est le rôle précis du relais de démarreur ?',r:''},{q:'Quelle est l\'intensité de courant typique appelée par un démarreur ?',r:''}], activitePratique:[ etape('Contrôles Préliminaires','25 min',['Vérifier la tension de la batterie (>12,4 V).','Inspecter le serrage et l\'oxydation des cosses.','Contrôler la continuité des masses moteur et châssis.']), etape('Mesures Électriques','45 min',['Mesurer la tension en sortie du contacteur à clé (position Démarrage).','Vérifier la présence de 12V sur la borne 50 du démarreur.','Mesurer les chutes de tension sur le câble positif et la masse.','Mesurer l\'intensité au démarrage (doit être entre 150A et 300A).']), etape('Conclusion et Diagnostic','25 min',['Déterminer si la panne vient de la batterie, du câblage, du relais ou du démarreur.','Préconiser les actions correctives à mener.']) ], securiteRangement:['Prudence lors de la manipulation de la batterie','Ranger la pince ampèremétrique','Tracer toutes les mesures effectuées'], pointsCles:['Batterie OK en premier','Chutes de tension révélatrices','Intensité de démarrage élevée'] },
  4:{ id: 4, duree:'2h00', titre:'UE 207 • Contrôle du démarreur', situation:'Le diagnostic précédent a mis en cause le démarreur. Vous devez le déposer, le contrôler sur banc et le remplacer pour confirmer la panne et réparer le véhicule.', objectif:'Valider l’état du démarreur, le remplacer et effectuer un contrôle qualité. (Compétence C2.2)', materiel:['Banc de test démarreur','Multimètre','Jeu de clés','Pièces de rechange (solénoïde, balais)'], etudePrelim:[{q:'Décrire la constitution d\'un démarreur et le rôle de chaque composant.',r:''},{q:'Quels sont les symptômes caractéristiques d\'un démarreur hors service ?',r:''},{q:'Comment réaliser les tests d\'isolement et de continuité sur les enroulements ?',r:''}], activitePratique:[ etape('Dépose du Démarreur','30 min',['Débrancher la batterie.','Déposer les câbles d\'alimentation et les fixations du démarreur.','Extraire le démarreur du véhicule.']), etape('Contrôles des Composants','40 min',['Mesurer les résistances de l\'inducteur et de l\'induit.','Vérifier l\'isolement à la masse.','Contrôler l\'état du solénoïde et des balais.']), etape('Test sur Banc','35 min',['Mesurer l\'intensité à vide (doit être < 80A).','Écouter les bruits anormaux et vérifier la rotation.','Effectuer un test en charge si le banc le permet.']), etape('Repose et Validation','15 min',['Respecter les couples de serrage.','Effectuer un test de démarrage sur le véhicule.']) ], securiteRangement:['Batterie débranchée avant toute intervention','Ranger et nettoyer le banc après usage','Nettoyer les pièces et le poste de travail'], pointsCles:['Contrôles d\'isolement','Usure des balais','Validation sur banc'] },
  5:{ id: 5, duree:'1h45', titre:'UE 201 • Contrôle circuit de charge', situation:'Le témoin de batterie s\'allume par intermittence. Le client craint une panne imminente. Vous devez contrôler le circuit de charge pour diagnostiquer le problème.', objectif:'Mesurer la tension de charge et les chutes de tension pour diagnostiquer. (Compétence C2.1)', materiel:['Multimètre','Pince ampèremétrique','Schéma électrique'], etudePrelim:[{q:'Pourquoi la tension de charge doit-elle se situer entre 13,8V et 14,4V ?',r:''},{q:'Quel est l\'effet d\'une courroie d\'accessoires détendue sur le circuit de charge ?',r:''},{q:'Quel est le rôle exact du régulateur de tension de l\'alternateur ?',r:''}], activitePratique:[ etape('Mesures de Tension','35 min',['Mesurer la tension batterie au repos, au ralenti, puis à 2000 tr/min.','Activer les consommateurs et vérifier la stabilité de la tension.','Mesurer les chutes de tension sur les câbles +B et la masse.']), etape('Inspection Visuelle','30 min',['Contrôler l\'état et la tension de la courroie d\'accessoires.','Vérifier l\'oxydation des bornes de la batterie et de l\'alternateur.','Écouter les bruits anormaux provenant de l\'alternateur.']), etape('Conclusion Diagnostique','20 min',['Déterminer si la panne vient des connexions, de la courroie ou de l\'alternateur.','Proposer les actions correctives.']) ], securiteRangement:['Attention aux pièces en rotation (courroie, ventilateur)','Débrancher la batterie avant toute dépose','Tracer les résultats de mesures'], pointsCles:['Tension de charge correcte','Connexions propres et serrées','Bonne tension de la courroie'] },
  6:{ id: 6, duree:'2h00', titre:'UE 206 • Contrôle de l\'alternateur', situation:'Suite au diagnostic précédent, l\'alternateur est suspecté. Vous devez le déposer pour un contrôle détaillé de ses composants internes (pont de diodes, régulateur).', objectif:'Tester et remplacer les composants défectueux de l’alternateur. (Compétence C2.2)', materiel:['Banc de test alternateur','Multimètre','Jeu de diodes','Régulateur neuf'], etudePrelim:[{q:'Quels sont les composants principaux d\'un alternateur ?',r:''},{q:'Comment tester un pont de diodes avec un multimètre ?',r:''},{q:'Quelle est la limite de tension du régulateur pour éviter de surcharger la batterie ?',r:''}], activitePratique:[ etape('Dépose de l\'Alternateur','25 min',['Débrancher la batterie, détendre la courroie.','Déconnecter les câbles et retirer les vis de fixation.','Extraire l\'alternateur du compartiment moteur.']), etape('Contrôles des Composants','45 min',['Mesurer la résistance du rotor (environ 3–5 Ω).','Contrôler la continuité du stator (environ 0,1–0,3 Ω).','Tester le pont de diodes (sens unique de passage).','Inspecter le régulateur et les balais.']), etape('Test sur Banc','35 min',['Vérifier que la tension de régulation est d\'environ 14V.','Mesurer l\'intensité nominale en charge.','Contrôler la stabilité sans surtension.']), etape('Repose et Validation','15 min',['Respecter les couples de serrage et la tension de la courroie.','Effectuer un test de charge sur le véhicule.']) ], securiteRangement:['Batterie débranchée','Ranger et nettoyer le banc de test','Nettoyer le poste de travail'], pointsCles:['Pont de diodes fonctionnel','Régulation de tension < 14,8V','Validation sur banc'] },
  7:{ id: 7, duree:'2h00', titre:'CA 209 • Fermeture centralisée', situation:'La télécommande de la voiture d\'un client ne fonctionne plus, l\'obligeant à utiliser la clé. Vous devez diagnostiquer le système de fermeture centralisée.', objectif:'Tester moteurs/commande, appairer la télécommande et effectuer les réglages. (Compétence C2.3)', materiel:['Valise de diagnostic','Multimètre','Télécommande neuve','Pile CR2032'], etudePrelim:[{q:'Expliquer le principe de fonctionnement de la fermeture centralisée.',r:''},{q:'Quelle est la différence entre une panne mécanique et une panne électrique sur un moteur de serrure ?',r:''},{q:'Quelle est la procédure générale d\'appairage d\'une nouvelle télécommande ?',r:''}], activitePratique:[ etape('Diagnostic Initial','25 min',['Tester le fonctionnement avec la clé et la télécommande.','Lire les codes défauts dans le calculateur confort.','Vérifier la tension de la pile de la télécommande (doit être > 3V).']), etape('Contrôle des Actionneurs','50 min',['Vérifier l\'alimentation des moteurs de serrure (inversion de polarité).','Tester les microrupteurs de position de porte.','Contrôler l\'état et la continuité des faisceaux de porte.']), etape('Synchronisation de la Télécommande','30 min',['Vérifier l\'émission du signal (infrarouge ou radio).','Suivre la procédure d\'appairage du constructeur.','Valider le fonctionnement de la nouvelle télécommande.']) ], securiteRangement:['Attention aux clips de garniture de porte','Protéger les vitres lors des manipulations','Tracer la procédure d\'appairage'], pointsCles:['Tester la pile en premier','Moteur ou mécanisme ?','Procédure d\'appairage'] },
  8:{ id: 8, duree:'2h00', titre:'UE 211 • Diagnostic d\'un circuit électrique (feux stop)', situation:'Un client a été averti que ses feux stop ne s\'allument plus. Vous devez appliquer une méthode rigoureuse pour localiser et réparer la panne.', objectif:'Isoler la panne, la corriger et réaliser un contrôle qualité. (Compétence C2.4)', materiel:['Schéma électrique','Multimètre','Jeu de fusibles','Jeu d\'ampoules'], etudePrelim:[{q:'Décrire le chemin du courant +12V de la batterie jusqu\'aux feux stop.',r:''},{q:'Comment tester le bon fonctionnement du contacteur de pédale de frein ?',r:''},{q:'Expliquer la méthode de diagnostic dite du "point-milieu".',r:''}], activitePratique:[ etape('Contrôles Simples','25 min',['Vérifier les ampoules et le fusible correspondant.','Inspecter les connecteurs et les points de masse.','Tester le fonctionnement du contacteur de pédale.']), etape('Mesures Ciblées','45 min',['Mesurer la tension en entrée et en sortie du contacteur.','Vérifier la continuité du faisceau jusqu\'aux feux arrière.','Mesurer la chute de tension au niveau de la masse.']), etape('Validation de la Réparation','20 min',['Effectuer la réparation nécessaire.','Recontrôler le fonctionnement complet des feux stop.']) ], securiteRangement:['Couper le contact avant de manipuler le circuit','Respecter les calibres des fusibles et sections de fils','Fixer correctement les faisceaux réparés'], pointsCles:['Utiliser le schéma électrique','Méthode du point-milieu','Mesures de tension et continuité'] },
  9:{ id: 9, duree:'1h45', titre:'UE 210 • Câblage des clignotants', situation:'Sur une maquette pédagogique, vous devez réaliser de A à Z le câblage fonctionnel du circuit de clignotants et de feux de détresse.', objectif:'Câbler un circuit conforme et effectuer le réglage de la fréquence. (Compétence C2.3)', materiel:['Centrale clignotante','Commodo','Ampoules 21W','Câbles et cosses diverses'], etudePrelim:[{q:'Quelle est la différence entre une centrale thermique et une centrale électronique ?',r:''},{q:'Quelle est la fréquence de clignotement nominale (en coups par minute) ?',r:''},{q:'Quel est l\'effet d\'une ampoule grillée sur la fréquence de clignotement ?',r:''}], activitePratique:[ etape('Câblage sur Maquette','40 min',['Alimenter la centrale clignotante.','Câbler le commodo pour les directions Gauche/Droite.','Relier les ampoules Avant/Arrière de chaque côté.']), etape('Tests et Validation','40 min',['Vérifier le fonctionnement des clignotants Gauche et Droit.','Câbler et tester la fonction feux de détresse (warning).','Mesurer la fréquence de clignotement et la valider.']), etape('Application sur Véhicule','25 min',['Identifier la centrale clignotante sur un véhicule réel.','Contrôler le relais et les ampoules correspondantes.','Tracer le schéma de câblage réalisé.']) ], securiteRangement:['Débrancher la maquette après usage','Ranger les câbles et cosses','Archiver le schéma de câblage'], pointsCles:['Rôle de la centrale','Warning = test global','Fréquence de clignotement'] },
  10:{ id: 10, duree:'2h00', titre:'UE 205 • Câblage d\'un circuit de moto-ventilateur', situation:'Sur maquette, vous devez câbler un circuit de refroidissement moteur complet, incluant Petite Vitesse (PV) et Grande Vitesse (GV) via des relais et une résistance.', objectif:'Réaliser un montage, valider les déclenchements et effectuer les réglages. (Compétence C2.3)', materiel:['Relais double','Résistance de puissance (PV)','Sonde de température','Fusibles 40A'], etudePrelim:[{q:'Expliquer le principe de la petite et grande vitesse de ventilation.',r:''},{q:'Quel est le rôle de la résistance série dans le circuit Petite Vitesse ?',r:''},{q:'Comment la commande peut-elle être assurée par un thermocontact ou par le calculateur ?',r:''}], activitePratique:[ etape('Câblage du Circuit','50 min',['Câbler l\'alimentation + batterie via le fusible de puissance.','Brancher les relais Petite Vitesse et Grande Vitesse.','Intégrer la résistance pour la commande PV du motoventilateur.']), etape('Tests et Mesures','45 min',['Mesurer l\'intensité en PV (environ 10A).','Mesurer l\'intensité en GV (environ 25A).','Valider le déclenchement via le thermocontact ou la commande de diagnostic.']), etape('Application sur Véhicule','25 min',['Identifier les relais et la résistance sur un véhicule réel.','Vérifier les déclenchements PV/GV.']) ], securiteRangement:['Débrancher la maquette après usage','Fixer solidement les câbles de puissance','Tracer le schéma réalisé'], pointsCles:['Résistance = PV','Intensités distinctes','Rôle des relais'] },
  11:{ id: 11, duree:'1h45', titre:'ME 205 • Contrôle circuit moto-ventilation refroidissement', situation:'Un véhicule a tendance à chauffer dans les embouteillages. Vous devez contrôler le circuit de moto-ventilation pour diagnostiquer le problème.', objectif:'Valider PV/GV, les températures de déclenchement et effectuer un contrôle qualité. (Compétence C2.4)', materiel:['Valise de diagnostic','Multimètre','Thermomètre infrarouge','Schéma électrique'], etudePrelim:[{q:'Quelles sont les températures de déclenchement typiques pour la PV et la GV ?',r:''},{q:'Quelles sont les conséquences d\'un défaut de moto-ventilateur ?',r:''},{q:'Comment l\'ECU commande-t-il les relais en fonction de la sonde de température ?',r:''}], activitePratique:[ etape('Test Fonctionnel','35 min',['Laisser le moteur monter en température.','Observer le déclenchement de la PV, puis de la GV.','Mesurer le temps de refroidissement.']), etape('Contrôles Électriques','40 min',['Vérifier l\'alimentation 12V des relais.','Mesurer les intensités consommées en PV et GV.','Contrôler la chute de tension à la masse (< 0,3V).']), etape('Diagnostic via Outil','30 min',['Lire les codes défauts dans l\'ECU.','Forcer l\'activation de la PV et de la GV via la valise.','Établir des préconisations de réparation.']) ], securiteRangement:['Laisser refroidir le moteur avant intervention','Débrancher la valise de diagnostic proprement','Tracer les résultats'], pointsCles:['Séquence PV → GV','Intensités cohérentes','Températures de déclenchement'] },
  12:{ id: 12, duree:'2h00', titre:'CA 210 • Vitres électriques', situation:'La vitre électrique côté passager ne fonctionne plus, ni depuis la commande conducteur, ni depuis la commande passager. Vous devez en trouver la cause.', objectif:'Discriminer panne moteur/mécanique/commande et remplacer l’élément défectueux. (Compétence C2.2)', materiel:['Multimètre','Graisse silicone','Outils de dégarnissage','Schéma électrique'], etudePrelim:[{q:'Expliquer le principe d\'un lève-vitre et de son système anti-pincement.',r:''},{q:'Pourquoi une initialisation est-elle nécessaire après une coupure de batterie ?',r:''},{q:'Quels sont les symptômes d\'un moteur HS par rapport à un mécanisme cassé ?',r:''}], activitePratique:[ etape('Diagnostic Initial','25 min',['Tester les commandes conducteur et passager.','Vérifier le fusible correspondant aux lève-vitres.','Contrôler que le verrouillage enfant n\'est pas activé.']), etape('Contrôles Électriques','45 min',['Mesurer l\'alimentation du moteur (inversion +/- 12V).','Mesurer la résistance du moteur (environ 5–15 Ω).','Tester la continuité de l\'interrupteur inverseur.']), etape('Contrôles Mécaniques','35 min',['Inspecter l\'état des rails et des câbles.','Graisser le mécanisme si nécessaire.','Vérifier l\'état des butées.']), etape('Initialisation','15 min',['Effectuer des cycles complets montée/descente pour réapprendre les butées.','Tester le bon fonctionnement de l\'anti-pincement.']) ], securiteRangement:['Protéger les garnitures de porte','Protéger les vitres lors des manipulations','Tracer l\'intervention'], pointsCles:['Panne moteur vs mécanique','Nécessité d\'initialisation','Graissage du mécanisme'] },
  13:{ id: 13, duree:'1h45', titre:'CA 208 • Diagnostic essuie-vitre', situation:'Les essuie-glaces d\'un véhicule fonctionnent de manière erratique : la vitesse intermittente ne marche plus et ils ne reviennent pas à leur position de repos ("parking").', objectif:'Identifier le défaut (moteur/relais/commodo/parking) et le réparer. (Compétence C2.2)', materiel:['Multimètre','Relais de test','Jeu de fusibles','Schéma électrique'], etudePrelim:[{q:'Expliquer le principe des différentes vitesses (PV/GV/intermittent).',r:''},{q:'Quel est le rôle du contacteur de fin de course (position "parking") ?',r:''},{q:'Comment la commande intermittente est-elle gérée (relais ou ECU) ?',r:''}], activitePratique:[ etape('Diagnostic Fonctionnel','25 min',['Tester toutes les positions du commodo.','Vérifier le fusible correspondant.','Tester le fonctionnement du lave-glace.']), etape('Contrôles Électriques','50 min',['Vérifier l\'alimentation du moteur d\'essuie-glace.','Tester la continuité des différentes positions du commodo.','Contrôler le relais d\'intermittence.','Vérifier le signal du contacteur de "parking".']), etape('Contrôles Mécaniques','25 min',['Inspecter l\'état de la biellette et des axes.','Vérifier la tension du ressort des bras.','Contrôler le fonctionnement de la pompe de lave-glace.']) ], securiteRangement:['Couper le contact avant intervention','Ranger les caches et protections','Tracer le diagnostic'], pointsCles:['Test du commodo','Rôle du relais','Position "parking"'] },
  14:{ id: 14, duree:'3h00', titre:'MTE 203 • Remplacement distribution et pompe à eau', situation:'Un véhicule arrive à l\'échéance de remplacement de sa courroie de distribution. L\'intervention inclut préventivement le remplacement de la pompe à eau.', objectif:'Respecter la procédure constructeur, le calage et remplacer les pièces. (Compétence C2.2)', materiel:['Kit de distribution','Jeu de piges de calage','Clé dynamométrique','Liquide de refroidissement'], etudePrelim:[{q:'Pourquoi ne faut-il jamais tourner un vilebrequin seul sur un moteur DOHC (Double OverHead Camshaft) ?',r:''},{q:'Comment identifier les repères de calage PMH et AAC ?',r:''},{q:'Quels sont les couples de serrage les plus critiques sur cette intervention ?',r:''}], activitePratique:[ etape('Calage et Préparation','35 min',['Déposer les protections et carters.','Aligner les repères et insérer les piges de calage (vilo/AAC).','Marquer le sens de rotation de l\'ancienne courroie.']), etape('Remplacement des Composants','90 min',['Remplacer la pompe à eau, les galets tendeurs et enrouleurs.','Poser la nouvelle courroie en respectant le sens.','Appliquer la tension préconisée à l\'aide de l\'outil spécifique.','Effectuer deux tours moteur manuels et recontrôler le calage.']), etape('Finition et Validation','35 min',['Remplir et purger le circuit de refroidissement.','Contrôler l\'absence de fuites et de bruits anormaux.','Documenter l\'intervention avec traçabilité des pièces.']) ], securiteRangement:['Batterie débranchée','Respect strict des couples de serrage','Propreté des plans de joint'], pointsCles:['Calage moteur exact','Tension correcte de la courroie','Purge du circuit de refroidissement'] },
  15:{ id: 15, duree:'2h00', titre:'MTE 208 • Relevé compression / fuite', situation:'Un moteur essence manque de puissance et présente un ralenti instable. Un relevé des compressions est nécessaire pour évaluer l\'état de santé du moteur.', objectif:'Identifier un cylindre faible, localiser la fuite et réaliser un contrôle qualité. (Compétence C2.4)', materiel:['Compressiomètre','Testeur de fuite cylindre (air 6–8 bar)','Jeu de clés à bougie','Burette d\'huile'], etudePrelim:[{q:'Quelles sont les valeurs de compression attendues pour un moteur essence / diesel ?',r:''},{q:'Quel est l\'écart maximal admissible en pourcentage entre les cylindres ?',r:''},{q:'Comment interpréter un test de fuite (où l\'air s\'échappe-t-il) ?',r:''}], activitePratique:[ etape('Préparation du Test','25 min',['Faire chauffer le moteur.','Déposer toutes les bougies d\'allumage ou injecteurs.','Couper l\'allumage et l\'injection pour le test.']), etape('Test de Compression','50 min',['Actionner le démarreur pendant 5 à 7 tours pour chaque cylindre.','Relever et noter la pression de chaque cylindre.','Si un cylindre est faible, refaire le test après avoir injecté une cuillère d\'huile.']), etape('Test de Fuite','40 min',['Positionner le cylindre faible au PMH compression.','Injecter de l\'air sous pression (6 bar) et mesurer le pourcentage de fuite.','Écouter où l\'air s\'échappe : admission, échappement, reniflard, radiateur.']) ], securiteRangement:['Débrancher la batterie avant dépose','Respecter le couple de serrage des bougies','Tracer tous les relevés de mesure'], pointsCles:['Écart < 10% entre cylindres','Segmentation / Soupapes / Joint de culasse','Position PMH exacte'] },
  16:{ id: 16, duree:'2h00', titre:'AS 209 • Pré-contrôle technique', situation:'Un client souhaite faire un bilan complet de son véhicule avant de le présenter au contrôle technique officiel afin d\'éviter une contre-visite.', objectif:'Préparer le véhicule à la visite et réaliser un contrôle qualité final. (Compétence C2.4)', materiel:['Check-list officielle du contrôle technique','Lecteur EOBD','Luxmètre','Manomètre de pression pneus'], etudePrelim:[{q:'Quelles sont les principales rubriques soumises à contre-visite ?',r:''},{q:'Quels sont les seuils minimaux pour l\'usure des pneus et des freins ?',r:''},{q:'Un code défaut "permanent" dans le calculateur entraîne-t-il une contre-visite ?',r:''}], activitePratique:[ etape('Contrôles Visuels Majeurs','35 min',['Vérifier l\'éclairage, le vitrage, la corrosion perforante.','Inspecter l\'usure et la pression des pneus, les fuites visibles, l\'état de l\'échappement.']), etape('Mesures et Diagnostics','35 min',['Contrôler le réglage des phares avec le luxmètre.','Ajuster la pression des pneus.','Effectuer une lecture des codes défauts via la prise OBD.']), etape('Restitution et Préconisations','20 min',['Lister les défaillances constatées et leur niveau de gravité (mineur, majeur, critique).','Fournir des préconisations chiffrées de remise en état.']) ], securiteRangement:['Respecter les consignes de sécurité de l\'atelier','Ranger tous les outils de mesure','Archiver la fiche de pré-contrôle'], pointsCles:['Respect du cadre réglementaire','Calculateur "propre" (sans DTC)','Traçabilité des défauts'] },
  17:{ id: 17, duree:'2h00', titre:'MTE 210 • Pression d\'huile (circuit de lubrification)', situation:'Le témoin de pression d\'huile s\'allume par intermittence au ralenti. Vous devez mesurer la pression d\'huile pour diagnostiquer une éventuelle défaillance du circuit.', objectif:'Mesurer la pression ralenti/3000 tr/min et préparer le diagnostic. (Compétence C2.1)', materiel:['Manomètre de pression d\'huile (0–10 bar)','Adaptateur pour capteur de pression','Multimètre','Documentation technique (RTA)'], etudePrelim:[{q:'Quelles sont les pressions d\'huile typiques au ralenti et à 3000 tr/min ?',r:''},{q:'Quel est le rôle du clapet de décharge de la pompe à huile ?',r:''},{q:'Quels sont les symptômes et risques d\'une pression d\'huile trop faible ?',r:''}], activitePratique:[ etape('Installation du Manomètre','30 min',['Faire chauffer le moteur.','Déposer le capteur de pression d\'huile et installer le manomètre.','Vérifier le niveau et la viscosité de l\'huile.']), etape('Mesures de Pression','50 min',['Mesurer la pression au ralenti (doit être > 1–2 bar).','Mesurer la pression à 3000 tr/min (doit être de 3–6 bar).','Observer la stabilité de la pression.']), etape('Pré-Diagnostic','30 min',['Orienter le diagnostic : filtre colmaté, huile inadaptée, pompe, clapet, coussinets.','Corréler avec d\'éventuels codes défauts du calculateur.']) ], securiteRangement:['Attention à l\'huile chaude','Nettoyer toute fuite après dépose du manomètre','Tracer les pressions relevées'], pointsCles:['Pression cible atteinte','Stabilité de la pression','Identification des causes possibles'] },
  18:{ id: 18, duree:'2h00', titre:'MA 201 • Contrôle pression et débit essence', situation:'Un véhicule essence présente des pertes de puissance. Vous devez contrôler le circuit d\'alimentation basse pression pour vérifier la pompe à essence.', objectif:'Vérifier la régulation et le débit de la pompe avant une intervention. (Compétence C2.1)', materiel:['Manomètre de pression essence (0–6 bar)','Éprouvette graduée','Documentation technique (RTA)'], etudePrelim:[{q:'Quelle est la pression de rampe typique pour une injection indirecte ?',r:''},{q:'Quel est le rôle du clapet de maintien de pression ?',r:''},{q:'Quel est le débit minimal attendu pour une pompe à essence (en L/min) ?',r:''}], activitePratique:[ etape('Mesure de Pression','40 min',['Brancher le manomètre, mettre le contact : observer la montée en pression.','Mesurer au ralenti et en phase d\'accélération.','Couper le moteur et vérifier le maintien de la pression.']), etape('Mesure de Débit','40 min',['Dériver le circuit et mesurer le volume de carburant sur 30 secondes.','Extrapoler le débit en litres par minute.','Comparer la valeur à la préconisation constructeur.']) ], securiteRangement:['Assurer une bonne ventilation (vapeurs d\'essence)','Avoir un extincteur à proximité','Éliminer l\'essence récupérée dans les contenants adaptés'], pointsCles:['Pression correcte','Débit suffisant','Maintien de la pression'] },
  19:{ id: 19, duree:'2h00', titre:'MA 208 • Contrôle des injecteurs moteur diesel classique', situation:'Sur un moteur diesel ancienne génération, vous devez tester les injecteurs mécaniques sur un banc pour vérifier leur tarage et leur pulvérisation.', objectif:'Tester au banc, remplacer et conclure. (Compétence C2.2)', materiel:['Banc de test d\'injecteurs','Gasoil propre','Éprouvettes graduées'], etudePrelim:[{q:'Quelle est la pression d\'ouverture typique d\'un injecteur mécanique ?',r:''},{q:'Quels sont les critères d\'une bonne pulvérisation (cône, homogénéité) ?',r:''},{q:'Quels sont les signes d\'un injecteur défectueux (goutte, pissard) ?',r:''}], activitePratique:[ etape('Dépose des Injecteurs','30 min',['Nettoyer la zone de travail.','Déposer les lignes haute pression et les retours.','Extraire les injecteurs et leurs joints en cuivre.']), etape('Test sur Banc','60 min',['Mesurer la pression d\'ouverture (tarage).','Observer la forme du cône de pulvérisation.','Vérifier l\'étanchéité du nez de l\'injecteur.']), etape('Repose et Validation','30 min',['Utiliser des joints en cuivre neufs.','Serrer les injecteurs au couple préconisé.','Purger le circuit haute pression et démarrer.']) ], securiteRangement:['Protection des yeux obligatoire','Gérer les déchets de gasoil','Tracer les pressions relevées'], pointsCles:['Pression de tarage','Qualité de pulvérisation','Étanchéité parfaite'] },
  20:{ id: 20, duree:'2h30', titre:'F 204 • Remplacement maître-cylindre', situation:'La pédale de frein est spongieuse et s\'enfonce lentement. Le diagnostic indique une fuite interne du maître-cylindre. Vous devez le remplacer.', objectif:'Reposer et purger correctement pour réparer le circuit. (Compétence C2.2)', materiel:['Maître-cylindre neuf','Liquide de frein DOT 4','Appareil de purge sous pression','Clés à tuyauter'], etudePrelim:[{q:'Quel est le rôle d\'un maître-cylindre tandem (double circuit) ?',r:''},{q:'Quels sont les symptômes d\'une fuite interne par rapport à une fuite externe ?',r:''},{q:'Décrire la procédure de purge complète du circuit de freinage.',r:''}], activitePratique:[ etape('Dépose du Maître-cylindre','40 min',['Aspirer le liquide de frein du réservoir.','Débrancher les canalisations et les fixations.','Extraire le maître-cylindre.']), etape('Repose et Purge','60 min',['Pré-remplir le maître-cylindre neuf sur l\'établi.','Raccorder les canalisations.','Purger le circuit complet en respectant l\'ordre : ARD→ARG→AVD→AVG.']), etape('Validation','10 min',['Vérifier que la pédale est ferme.','Contrôler l\'absence totale de fuites.']) ], securiteRangement:['Liquide de frein corrosif (protéger la peinture)','Nettoyer immédiatement toute projection','Éliminer le liquide usagé via la filière agréée'], pointsCles:['Purge complète du circuit','Niveau du réservoir correct','Pédale dure et stable'] },
  21:{ id: 21, duree:'1h45', titre:'F 206 • Relevé de pressions de freinage', situation:'Pour diagnostiquer un déséquilibre de freinage, vous devez mesurer les pressions hydrauliques aux 4 roues à l\'aide de manomètres.', objectif:'Détecter les déséquilibres, localiser la cause et effectuer le réglage. (Compétence C2.3)', materiel:['Jeu de 4 manomètres haute pression','Adaptateurs de purge','Assistant pour actionner la pédale'], etudePrelim:[{q:'Quelles sont les pressions de freinage typiques à l\'avant et à l\'arrière ?',r:''},{q:'Quel est le rôle du répartiteur de freinage ?',r:''},{q:'Quel est l\'écart de pression admissible entre les roues d\'un même essieu ?',r:''}], activitePratique:[ etape('Installation des Manomètres','30 min',['Brancher les manomètres sur les vis de purge de chaque roue.','Repérer clairement chaque manomètre (AVD, AVG, ARD, ARG).']), etape('Mesures de Pression','45 min',['Relever les pressions à mi-course et en pression maximale.','Comparer les pressions Avant/Arrière et Gauche/Droite.','Identifier et interpréter les déséquilibres.']) ], securiteRangement:['Déposer les manomètres proprement','Effectuer une purge si de l\'air a été introduit','Tracer tous les relevés'], pointsCles:['Symétrie des pressions','Pression AR < Pression AV','Rôle du répartiteur'] },
  22:{ id: 22, duree:'2h00', titre:'F 208 • Diagnostic du circuit de freinage avec passage au banc', situation:'Un véhicule doit passer au banc de freinage pour évaluer objectivement son efficacité et son équilibre avant le contrôle technique.', objectif:'Mesurer efficacité/équilibre/frein à main et effectuer un contrôle qualité. (Compétence C2.4)', materiel:['Banc de freinage à rouleaux','Fiche de résultats imprimée'], etudePrelim:[{q:'Quelle est l\'efficacité de freinage minimale requise (%) ?',r:''},{q:'Quel est le déséquilibre maximal toléré sur un même essieu (%) ?',r:''},{q:'Quelle est l\'efficacité minimale pour le frein de stationnement (%) ?',r:''}], activitePratique:[ etape('Préparation du Test','20 min',['Vérifier les pressions des pneus.','Effectuer une inspection visuelle rapide des freins.','Suivre les consignes de sécurité du banc.']), etape('Test Essieu Avant','30 min',['Mesurer les forces de freinage AVD et AVG.','Calculer le déséquilibre avant.']), etape('Test Essieu Arrière','30 min',['Mesurer les forces ARD et ARG.','Tester l\'efficacité du frein de stationnement.']), etape('Interprétation des Résultats','20 min',['Vérifier la conformité : efficacité totale > 50%, déséquilibre < 30%, frein à main > 16%.']) ], securiteRangement:['Sortir prudemment du banc de freinage','Ranger les équipements','Archiver la fiche de résultats'], pointsCles:['Efficacité de freinage','Équilibre G/D','Efficacité du frein à main'] },
  23:{ id: 23, duree:'2h00', titre:'F 209 • Diagnostic assistance au freinage', situation:'La pédale de frein d\'un véhicule est anormalement dure. Vous devez diagnostiquer le système d\'assistance au freinage (servofrein).', objectif:'Tester l’assistance, vérifier dépression/pression et remplacer les composants. (Compétence C2.2)', materiel:['Vacuomètre', 'Manomètre de pression', 'Valise de diagnostic', 'Schéma du système'], etudePrelim:[{ q: 'Expliquer le principe du servofrein à dépression.', r: '' },{ q: 'Quelle est la valeur de dépression normale (en mbar) dans le circuit ?', r: '' },{ q: 'Quels sont les symptômes caractéristiques d\'une assistance au freinage défaillante ?', r: '' }], activitePratique:[etape('Test Fonctionnel du Servofrein', '30 min', ['Moteur éteint, pomper plusieurs fois sur la pédale puis la maintenir enfoncée. Démarrer : la pédale doit s\'enfoncer légèrement.','Tester le maintien de la dépression après coupure du moteur.']),etape('Mesure de la Dépression', '40 min', ['Brancher le vacuomètre sur la durite entre la source (collecteur/pompe à vide) et le servofrein.','Mesurer la dépression au ralenti (environ -500 à -700 mbar).','Contrôler l\'étanchéité du clapet anti-retour et des durites.']),etape('Diagnostic Systèmes Hydrauliques (si équipé)', '35 min', ['Lire les codes défauts via la valise.','Mesurer la pression dans l\'accumulateur hydraulique.','Tester le capteur de pression et le contacteur de pédale.'])], securiteRangement:['Raccorder correctement les durites de dépression', 'Ranger les instruments de mesure', 'Tracer les mesures relevées'], pointsCles:['La pédale s\'enfonce au démarrage', 'Dépression suffisante et stable', 'Clapet anti-retour étanche'] },
  24:{ id: 24, duree:'4h00', titre:'T 201 • Dépose-pose embrayage', situation:'Un véhicule patine à l\'accélération. Le diagnostic confirme l\'usure de l\'embrayage. Vous êtes chargé du remplacement complet du kit (disque, mécanisme, butée).', objectif:'Remplacer le kit et valider un débrayage correct après réparation. (Compétence C2.2)', materiel:['Kit d\'embrayage neuf', 'Centreur d\'embrayage', 'Cric de fosse pour boîte de vitesses', 'Clé dynamométrique'], etudePrelim:[{ q: 'Expliquer les symptômes : patinage, broutage, point de touche haut.', r: '' },{ q: 'Pourquoi le centrage du disque est-il une étape critique ?', r: '' },{ q: 'Quels sont les couples de serrage à respecter et quelle huile de boîte utiliser ?', r: '' }], activitePratique:[etape('Dépose de la Boîte de Vitesses', '80 min', ['Lever et sécuriser le véhicule sur pont.','Déposer les cardans, supports, tringlerie et connexions électriques.','Extraire la boîte de vitesses à l\'aide du cric de fosse.']),etape('Remplacement de l\'Embrayage', '50 min', ['Remplacer la butée et graisser son guide.','Centrer le disque neuf avec l\'outil spécifique.','Serrer le mécanisme en étoile et au couple préconisé.']),etape('Repose et Validation', '80 min', ['Accoupler et reposer la boîte de vitesses, les supports et cardans.','Rétablir la commande d\'embrayage (câble ou purge si hydraulique).','Effectuer un essai routier pour valider le point de touche et l\'absence de patinage.'])], securiteRangement:['Sécurité absolue lors du levage et de la dépose de la BV', 'Couples de serrage scrupuleusement respectés', 'Ranger et nettoyer l\'outillage'], pointsCles:['Centrage parfait du disque','Commande d\'embrayage correctement réglée','Essai routier validé'] },
  25:{ id: 25, duree:'2h30', titre:'T 208 • Remplacement commande d\'embrayage hydraulique', situation:'La pédale d\'embrayage est molle et reste parfois au plancher. Une fuite est détectée sur le circuit hydraulique (émetteur ou récepteur).', objectif:'Remplacer l’organe défectueux et purger correctement. (Compétence C2.2)', materiel:['Émetteur ou récepteur d\'embrayage', 'Liquide de frein DOT 4', 'Appareil de purge', 'Clés à tuyauter'], etudePrelim:[{ q: 'Expliquer le principe de la commande d\'embrayage hydraulique.', r: '' },{ q: 'Décrire les symptômes d\'une pédale molle ou d\'une fuite.', r: '' },{ q: 'Quelle est la procédure exacte pour purger ce circuit ?', r: '' }], activitePratique:[etape('Dépose de l\'Organe Défectueux', '40 min', ['Aspirer le liquide du bocal.','Débrancher les durites et les fixations.','Extraire l\'émetteur (côté pédalier) ou le récepteur (sur la boîte).']),etape('Repose et Purge du Circuit','60 min', ['Monter l\'élément neuf.','Remplir avec du liquide de frein DOT 4 neuf.','Purger le circuit jusqu\'à disparition complète des bulles d\'air.']),etape('Validation de l\'Intervention', '10 min', ['Vérifier que la course de la pédale est normale.','Contrôler que les vitesses passent bien à l\'arrêt.','Documenter l\'intervention.'])], securiteRangement:['Liquide de frein corrosif (protéger la peinture)','Nettoyage immédiat de toute projection','Élimination des déchets selon les normes environnementales'], pointsCles:['Purge complète du circuit','Absence totale de fuites','Course de pédale validée'] },
  26:{ id: 26, duree:'3h00', titre:'T 206 • Découverte de la boîte de vitesses', situation:'Sur une boîte de vitesses déposée et destinée à la formation, vous devez effectuer un démontage partiel pour analyser son fonctionnement interne.', objectif:'Identifier pignons, synchros, arbres et fourchettes pour préparer une intervention. (Compétence C2.1)', materiel:['Boîte de vitesses didactique', 'Jeu d\'extracteurs', 'Bac de nettoyage', 'Schéma éclaté de la BV'], etudePrelim:[{ q: 'Quel est le rôle principal d\'une boîte de vitesses ?', r: '' },{ q: 'Quelle est la fonction d\'un synchroniseur ?', r: '' },{ q: 'Décrire la constitution des arbres (primaire, secondaire) et des pignons.', r: '' }], activitePratique:[etape('Ouverture de la Boîte','50 min', ['Vidanger l\'huile de la boîte.','Ouvrir le carter de sélection et le carter principal.','Repérer les différents ensembles (arbres, différentiel).']),etape('Analyse des Composants','80 min', ['Examiner l\'usure des bagues de synchro et des crabots.','Contrôler le jeu des roulements d\'arbres.','Observer le fonctionnement de la sélection et des fourchettes.']),etape('Remontage Partiel','70 min', ['Remplacer les joints et les circlips si nécessaire.','Appliquer la graisse adaptée sur les points de friction.','Contrôler la libre rotation des arbres et le bon passage des rapports.'])], securiteRangement:['Recycler l\'huile de boîte usagée','Nettoyer les pièces avant analyse','Ranger l\'outillage spécifique (extracteurs)'], pointsCles:['Méthode et rigueur','Repérage des pièces','Analyse des jeux fonctionnels'] },
  27:{ id: 27, duree:'3h30', titre:'T 205 • Échange synchro 5e', situation:'Un véhicule présente un craquement systématique lors du passage de la 5ème vitesse. Le diagnostic pointe vers un synchroniseur usé. Vous devez le remplacer.', objectif:'Supprimer les craquements, remplacer le synchro et valider l’engagement. (Compétence C2.2)', materiel:['Kit synchroniseur 5ème vitesse', 'Jeu d\'extracteurs', 'Circlips neufs', 'Huile de boîte préconisée'], etudePrelim:[{ q: 'Pourquoi la 5ème vitesse est-elle souvent accessible via un carter dédié ?', r: '' },{ q: 'Quels sont les signes caractéristiques d\'un synchroniseur hors service ?', r: '' },{ q: 'Quels sont les jeux de fonctionnement admissibles ?', r: '' }], activitePratique:[etape('Accès au Pignon de 5ème','80 min', ['Déposer la boîte de vitesses du véhicule.','Ouvrir le carter de 5ème.','Bloquer l\'arbre pour desserrer l\'écrou de maintien.']),etape('Remplacement du Synchroniseur','60 min', ['Extraire le pignon et le baladeur de 5ème.','Remplacer les bagues et les ressorts du synchroniseur.','Contrôler les jeux avec un comparateur.']),etape('Validation de la Réparation','60 min', ['Remonter la boîte, faire le plein d\'huile.','Effectuer un essai routier en insistant sur le passage 4ème ↔ 5ème.','Documenter l\'intervention.'])], securiteRangement:['Recycler l\'huile usagée','Respecter les couples de serrage','Archiver les photos des étapes clés'], pointsCles:['Jeux fonctionnels corrects','Passage de vitesse doux','Absence de craquement validée'] },
  28:{ id: 28, duree:'1h30', titre:'L 204 • Effectuer un pré-contrôle géométrie', situation:'Avant de monter un véhicule sur le banc de géométrie 3D, un pré-contrôle rigoureux des trains roulants est indispensable pour garantir la fiabilité des mesures.', objectif:'Valider la préparation du véhicule avant de passer au banc 3D. (Compétence C2.1)', materiel:['Pont élévateur','Manomètre de pression pneus','Jauge de profondeur','Lampe d\'inspection'], etudePrelim:[{ q: 'Pourquoi faut-il impérativement régler la pression des pneus avant une mesure de géométrie ?', r: '' },{ q: 'Quels sont les jeux à contrôler impérativement (rotules, silentblocs) ?', r: '' },{ q: 'Quel est l\'impact d\'un jeu excessif sur la mesure des angles ?', r: '' }], activitePratique:[etape('Contrôle des Pneumatiques','20 min', ['Ajuster les pressions selon les préconisations.','Mesurer la profondeur des sculptures et vérifier l\'homogénéité.','Analyser les usures irrégulières (indices de défauts géométriques).']),etape('Contrôle du Train Avant','35 min', ['Vérifier l\'absence de jeu dans les rotules de direction et de suspension.','Inspecter l\'état des silentblocs de bras de suspension.','Contrôler le jeu des roulements de roue et l\'état des amortisseurs.']),etape('Contrôle du Train Arrière','25 min', ['Inspecter les bras de suspension et les biellettes.','Vérifier les fixations et l\'état des silentblocs.']) ], securiteRangement:['Descendre le pont en sécurité','Ranger les outils de mesure','Remplir la fiche de pré-contrôle'], pointsCles:['Pressions conformes','Absence totale de jeu','Véhicule prêt pour le banc'] },
  29:{ id: 29, duree:'2h00', titre:'L 207 • Contrôler géométrie. Régler le parallélisme', situation:'Un véhicule tire à droite et présente une usure anormale des pneus avant. Un contrôle et un réglage de la géométrie sont nécessaires.', objectif:'Obtenir des valeurs dans tolérances et réaliser le réglage avec volant centré. (Compétence C2.3)', materiel:['Banc de géométrie 3D','Clés de réglage pour biellettes','Fiches de valeurs constructeur'], etudePrelim:[{ q: 'Définir précisément parallélisme, carrossage et chasse.', r: '' },{ q: 'Quelles sont les conséquences d\'un mauvais réglage du parallélisme ?', r: '' },{ q: 'Pourquoi la charge du véhicule et la pression des pneus sont-elles cruciales ?', r: '' }], activitePratique:[etape('Mesure des Angles','50 min', ['Effectuer la compensation du voile des jantes.','Lancer la lecture des angles des trains Avant et Arrière.','Identifier les valeurs hors tolérances par rapport aux données constructeur.']),etape('Réglage du Parallélisme','40 min', ['Agir sur les biellettes de direction pour régler le parallélisme avant (et arrière si possible).','Effectuer des ajustements itératifs jusqu\'à obtenir des valeurs dans le vert.','Centrer parfaitement le volant pendant le réglage.']),etape('Validation Finale','10 min',['Imprimer les fiches de résultats Avant/Après.','Effectuer un court essai routier pour valider le comportement.'])], securiteRangement:['Serrer fermement les contre-écrous des biellettes','Ranger et protéger les capteurs 3D','Archiver les fiches de mesure'], pointsCles:['Symétrie des réglages','Réglage itératif','Volant parfaitement centré'] },
  30:{ id: 30, duree:'2h30', titre:'L 213 • Découverte et entretien suspension hydraulique', situation:'Un véhicule équipé d\'une suspension hydropneumatique présente une assiette incorrecte. Vous devez diagnostiquer et entretenir le système.', objectif:'Contrôler assiette, pressions sphères, et réparer le circuit. (Compétence C2.2)', materiel:['Manomètre haute pression','Station de gonflage azote','Liquide hydraulique (LHM/LDS)','Valise de diagnostic'], etudePrelim:[{ q: 'Expliquer le principe de la sphère (membrane azote/liquide).', r: '' },{ q: 'Quelles sont les pressions d\'azote typiques à l\'avant et à l\'arrière ?', r: '' },{ q: 'Quel est le rôle du régulateur de hauteur et de la pompe haute pression ?', r: '' }], activitePratique:[etape('Diagnostic de l\'Assiette','40 min', ['Mesurer les hauteurs de caisse aux 4 coins.','Comparer les valeurs aux tolérances constructeur.','Lire les codes défauts du calculateur de suspension.']),etape('Contrôle des Sphères','50 min', ['Dépressuriser le circuit.','Mesurer et regonfler la pression d\'azote de chaque sphère.','Remplacer une sphère si sa membrane est défectueuse.']),etape('Purge et Validation','50 min',['Compléter le niveau de liquide hydraulique.','Purger le circuit et actionner les différentes hauteurs.','Effectuer un essai pour valider le confort et la réactivité.'])], securiteRangement:['Utiliser uniquement de l\'azote pour le gonflage','Protection des yeux obligatoire','Éliminer le liquide hydraulique usagé correctement'], pointsCles:['Pression des sphères = confort','Correction d\'assiette','Purge du circuit'] },
  31:{ id: 31, duree:'1h45', titre:'MTE 202 • Contrôle étanchéité circuit de refroidissement', situation:'Une odeur de liquide de refroidissement est perceptible et le niveau baisse légèrement. Vous devez mettre le circuit sous pression pour localiser la fuite.', objectif:'Pressuriser, traquer les fuites et effectuer un contrôle qualité final. (Compétence C2.4)', materiel:['Pompe de test d\'étanchéité avec manomètre','Adaptateur de bouchon de radiateur','Traceur UV et lampe UV'], etudePrelim:[{ q: 'Quelle est la pression de fonctionnement d\'un circuit de refroidissement à chaud ?', r: '' },{ q: 'Quels sont les signes d\'un joint de culasse défectueux ?', r: '' },{ q: 'Quelle est la fonction du bouchon de radiateur taré ?', r: '' }], activitePratique:[etape('Test de Pression à Froid','50 min',['Moteur froid, mettre le circuit sous pression (1,2 à 1,6 bar).','Observer si la pression chute sur le manomètre et localiser les points humides.','Utiliser le traceur UV si la fuite est difficile à localiser.']),etape('Contrôles Complémentaires','30 min',['Utiliser un kit de détection de CO2 pour rechercher des gaz d\'échappement dans le liquide.','Vérifier l\'absence d\'huile émulsionnée ("mayonnaise").']),etape('Conclusion et Réparation','10 min',['Localiser précisément la fuite.','Effectuer un re-test d\'étanchéité après la réparation.']) ], securiteRangement:['Intervenir exclusivement sur moteur froid','Relâcher la pression avant toute déconnexion','Tracer la localisation de la fuite'], pointsCles:['La pression doit tenir','Traceur UV pour fuites discrètes','Recherche de CO2 pour le joint de culasse'] },
  32:{ id: 32, duree:'2h00', titre:'MA 202 • Diagnostic (prise en main matériel). Lecture paramètres', situation:'Pour initier un diagnostic sur un véhicule moderne, le chef d\'atelier vous demande de vous connecter au calculateur moteur pour lire les codes défauts et analyser les paramètres clés.', objectif:'Lire/interpréter les données pour préparer une intervention. (Compétence C2.1)', materiel:['Valise de diagnostic multimarque','Chargeur de maintien de tension','Adaptateurs OBD'], etudePrelim:[{ q: 'Quelle est la différence entre un code défaut (DTC), les données figées (freeze frame) et les paramètres (PIDs) ?', r: '' },{ q: 'Quels sont les paramètres clés à observer pour un diagnostic moteur ?', r: '' },{ q: 'Pourquoi est-il crucial de maintenir la tension de la batterie pendant un diagnostic ?', r: '' }], activitePratique:[etape('Connexion et Lecture Initiale','25 min',['Brancher l\'outil de diagnostic sur la prise OBD, identifier le véhicule et l\'ECU.','Effectuer une lecture complète et enregistrer tous les codes défauts.','Effacer les codes pour vérifier s\'ils réapparaissent au test suivant.']),etape('Analyse des Paramètres (PIDs)','40 min',['Sélectionner une liste de paramètres pertinents pour le symptôme observé.','Observer les valeurs au ralenti, puis en charge (essai routier ou accélération).','Comparer les valeurs aux données de référence de la RTA.']),etape('Validation du Diagnostic','15 min',['Reproduire les conditions d\'apparition du défaut.','Effectuer une nouvelle lecture des codes défauts.'])], securiteRangement:['Maintenir la charge de la batterie','Déconnecter proprement l\'outil de diagnostic','Archiver les journaux de diagnostic (logs)'], pointsCles:['Lecture des codes défauts','Analyse des paramètres pertinents','Validation par reproduction du défaut'] },
  33:{ id: 33, duree:'2h00', titre:'MA 210 • Identification structurelle du système d\'injection', situation:'Afin de pouvoir intervenir efficacement sur n\'importe quel système d\'injection, vous devez apprendre à identifier rapidement sa structure, ses capteurs et ses actionneurs.', objectif:'Localiser capteurs/actionneurs et flux air/carburant pour préparer une intervention. (Compétence C2.1)', materiel:['Schémas de systèmes d\'injection','Photos de moteurs','Étiquettes de repérage','Documentation technique (RTA)'], etudePrelim:[{ q: 'Quelles sont les différences entre injection essence mono-point, multi-points et directe ?', r: '' },{ q: 'Quelles sont les différences entre injection diesel à injection indirecte, Common Rail et injecteurs-pompes ?', r: '' },{ q: 'Quelles sont les principales boucles de régulation (lambda, EGR, turbo) ?', r: '' }], activitePratique:[etape('Analyse Système Essence','50 min',['Identifier l\'ECU, la rampe d\'injection et les injecteurs.','Localiser le débitmètre (MAF) ou capteur de pression (MAP), et les sondes lambda.','Repérer le boîtier papillon motorisé et le système d\'allumage.']),etape('Analyse Système Diesel','50 min',['Identifier la pompe haute pression, le rail commun et les injecteurs.','Localiser les capteurs PMH et AAC, indispensables au séquencement.','Repérer la vanne EGR et les capteurs du FAP (pression différentielle).'])], securiteRangement:['Ne jamais débrancher de composant moteur tournant','Protéger les connecteurs lors des interventions','Archiver les schémas étudiés'], pointsCles:['Cartographie mentale du système','Connaître les noms exacts des composants','Comprendre les flux d\'air et de carburant'] },
  34:{ id: 34, duree:'2h30', titre:'MA 204 • Diagnostic système injection essence', situation:'Un véhicule essence présente un manque de puissance et le témoin moteur est allumé. Vous devez mener un diagnostic complet du système d\'injection.', objectif:'Lier DTC/PIDs à des tests capteurs/actionneurs et réparer. (Compétence C2.2)', materiel:['Valise de diagnostic','Multimètre','Oscilloscope','Manomètre de pression essence'], etudePrelim:[{ q: 'Quels sont les symptômes typiques d\'un problème d\'injection essence ?', r: '' },{ q: 'Quelle est la pression de rampe attendue ?', r: '' },{ q: 'Quelle est la valeur attendue pour le ratio lambda ?', r: '' }], activitePratique:[etape('Lecture et Analyse Initiale','30 min',['Lire les codes défauts (DTC) et les données figées (freeze frame).','Analyser les paramètres clés : MAF/MAP, ratio lambda, pression de rampe (FRP).','Effacer les codes et effectuer un test pour voir ce qui revient.']),etape('Contrôle des Capteurs','60 min',['Mesurer la tension du débitmètre d\'air.','Vérifier la cohérence du capteur de pression d\'admission par rapport à la dépression.','Contrôler le signal des sondes lambda amont et aval.','Visualiser les signaux des capteurs PMH et AAC à l\'oscilloscope.']),etape('Contrôle des Actionneurs','40 min',['Mesurer la résistance et le signal de commande des injecteurs.','Tester le fonctionnement du boîtier papillon motorisé et de la vanne EGR.','Vérifier le régulateur de pression de carburant.'])], securiteRangement:['Effectuer les déconnexions proprement','Ranger les appareils de mesure après usage','Tracer toutes les étapes du diagnostic'], pointsCles:['Corrélation DTC et paramètres','Les capteurs confirment le symptôme','Les actionneurs valident la panne'] },
  35:{ id: 35, duree:'2h00', titre:'ME 202 • Contrôle du circuit d\'allumage', situation:'Un véhicule essence a des ratés à l\'accélération. Le diagnostic s\'oriente vers le circuit d\'allumage. Vous devez contrôler les bougies, câbles et bobines.', objectif:'Diagnostiquer les ratés, réparer et valider l\'étincelle. (Compétence C2.2)', materiel:['Multimètre','Oscilloscope','Clé à bougie dynamométrique','Testeur d\'étincelle'], etudePrelim:[{ q: 'Quels sont les différents types d\'allumage (statique, DIS, bobine-crayon) ?', r: '' },{ q: 'Quelle est la tension typique d\'une étincelle (en kV) ?', r: '' },{ q: 'Quels sont les signes d\'un défaut d\'allumage (ratés, consommation) ?', r: '' }], activitePratique:[etape('Pré-Diagnostic','20 min',['Lire les codes défauts (ex: P030x pour raté d\'allumage).','Inspecter visuellement les bougies et les bobines.','Écouter le moteur au ralenti pour détecter les ratés.']),etape('Contrôle des Bobines','45 min',['Mesurer les résistances des enroulements primaire et secondaire.','Vérifier l\'isolement.','Visualiser le signal de commande primaire à l\'oscilloscope.','Effectuer un test d\'étincelle.']),etape('Contrôle des Bougies','40 min',['Inspecter l\'état des électrodes et mesurer l\'écartement.','Analyser la couleur (indice de richesse).','Remplacer les bougies défectueuses en respectant le couple de serrage.'])], securiteRangement:['Attention à la haute tension (HT)','Respecter les couples de serrage des bougies','Tracer le diagnostic'], pointsCles:['Bobine fonctionnelle','Bougie en bon état','Analyse des courbes à l\'oscilloscope'] },
  36:{ id: 36, duree:'1h45', titre:'ME 203 • Contrôle du circuit de pré-post chauffage', situation:'Un moteur diesel démarre mal à froid et fume blanc. Vous devez contrôler le circuit de préchauffage pour trouver la cause.', objectif:'Tester les bougies, relais et temporisation puis effectuer le réglage. (Compétence C2.3)', materiel:['Multimètre','Pince ampèremétrique','Chronomètre','Schéma électrique'], etudePrelim:[{ q: 'Quel est le rôle du préchauffage sur un moteur diesel ?', r: '' },{ q: 'Quelle est l\'intensité typique consommée par une bougie de préchauffage ?', r: '' },{ q: 'Comment la durée de préchauffage varie-t-elle en fonction de la température ?', r: '' }], activitePratique:[etape('Contrôle du Circuit Global','35 min',['Vérifier l\'allumage du voyant au tableau de bord et son temps.','Mesurer la tension sur la barre d\'alimentation des bougies.','Mesurer l\'intensité totale consommée par le circuit.']),etape('Contrôle des Bougies','45 min',['Mesurer la résistance de chaque bougie.','Mesurer l\'intensité individuelle de chaque bougie.','Inspecter visuellement l\'extrémité des bougies.']),etape('Contrôle du Boîtier de Préchauffage','20 min',['Vérifier l\'activation et la commande du relais.','Contrôler le maintien de la tension en post-chauffage.'])], securiteRangement:['Débrancher la batterie avant dépose','Respecter les couples de serrage','Tracer les mesures'], pointsCles:['Homogénéité des bougies','Commande fonctionnelle','Temporisation conforme'] },
  37:{ id: 37, duree:'2h00', titre:'MP 201 • Analyser les gaz d\'échappement essence et diesel', situation:'Dans le cadre d\'un diagnostic pollution, vous devez utiliser l\'analyseur de gaz et l\'opacimètre pour évaluer les émissions d\'un véhicule.', objectif:'Comparer aux normes, cibler l’organe défaillant et effectuer un contrôle qualité. (Compétence C2.4)', materiel:['Analyseur 4/5 gaz','Opacimètre','Thermomètre infrarouge','Documentation technique (RTA)'], etudePrelim:[{ q: 'Quelles sont les valeurs repères pour CO, HC et lambda sur un moteur essence ?', r: '' },{ q: 'Comment interpréter l\'opacité des fumées d\'un moteur diesel (en % ou m-1) ?', r: '' },{ q: 'Quelles sont les causes possibles d\'un taux de NOx élevé ?', r: '' }], activitePratique:[etape('Analyse sur Moteur Essence','50 min',['Faire chauffer le moteur et le catalyseur.','Effectuer les mesures au ralenti, puis au ralenti accéléré (2500 tr/min).','Analyser les valeurs de CO, HC, O2, CO2 et lambda.']),etape('Analyse sur Moteur Diesel','50 min',['Utiliser l\'opacimètre pour mesurer l\'opacité des fumées (test en 3 accélérations).','Analyser la valeur K (m-1) ou le pourcentage d\'opacité.','Interpréter les graphiques d\'accélération.'])], securiteRangement:['Assurer une bonne ventilation de l\'atelier','Nettoyer les sondes des appareils après usage','Archiver les tickets de résultats'], pointsCles:['Valeurs CO/HC/Lambda','Opacité des fumées','Identification de l\'organe ciblé'] },
  38:{ id: 38, duree:'1h30', titre:'MP 202 • Contrôle des sondes lambda', situation:'Le témoin moteur est allumé et le diagnostic pointe vers un défaut sur les sondes lambda. Vous devez les contrôler pour valider le diagnostic.', objectif:'Vérifier oscillations, temps de réponse et effectuer les réglages. (Compétence C2.3)', materiel:['Valise de diagnostic','Oscilloscope','Multimètre','Clé à sonde lambda'], etudePrelim:[{ q: 'Quel est le rôle respectif de la sonde amont et de la sonde aval ?', r: '' },{ q: 'Pourquoi la sonde amont doit-elle osciller rapidement entre 0,1V et 0,9V ?', r: '' },{ q: 'Quels sont les indices d\'une sonde "lente" ou "paresseuse" ?', r: '' }], activitePratique:[etape('Lecture des Paramètres','25 min',['Observer les signaux des sondes amont et aval via l\'outil de diagnostic.','Vérifier que la boucle de régulation est fermée à chaud.','Effectuer des tests d\'enrichissement / appauvrissement.']),etape('Mesures Physiques','35 min',['Visualiser le signal de la sonde amont à l\'oscilloscope (pentes nettes).','Mesurer la résistance et l\'alimentation du réchauffeur de sonde.','Vérifier que le signal de la sonde aval est stable si le catalyseur est efficace.']),etape('Diagnostic Final','25 min',['Une sonde amont lente ou non réactive doit être remplacée.','Une sonde aval qui oscille indique un catalyseur hors service.'])], securiteRangement:['Prudence avec l\'échappement chaud','Respecter le couple de serrage de la sonde','Tracer les signaux observés'], pointsCles:['Sonde amont oscille','Sonde aval contrôle le catalyseur','Temps de réponse de la sonde'] }
};




    